<span class="line"><span style="color:#BABED8;">const cookie = require(</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">cookie</span><span style="color:#89DDFF;">&quot;</span><span style="color:#BABED8;">);  </span><span style="color:#676E95;font-style:italic;">// ???</span></span>
